    user:null,
    username:'',
    password:'',
    loginAndPasswordIsValid:false,
    callback,
};

function UserReducer(state = initialState,action) {

    switch (action.type) {

        case USER.CREATE_USER.type:

            return state;
        break;

        case USER.LOGIN.type:

