import React from 'react';
import store from '../store/store';
import USER from '../actions/user-action';

export default class CreateAccount extends React.Component {

    constructor(props){
        
        super(props);

        this.state = {
            username:'',
            password:'',
            mail:''
        };

        this.send = this.send.bind(this);
        this.username = React.createRef();
    }

    handleChange(event) {

        const target = event.target,
              name = target.name,
              value = target.value;

        this.setState({[name]:value});
    }

    send() {

        USER.CREATE_USER.User = {
            username:this.state.username,
            password: this.state.password
        };

        store.dispatch(USER.CREATE_USER);
    }

    render() {
        const {state} = this;

        return(
            <div>
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12'>
                        <span className='close'>x</span>
                        <br />
                        <h3 className='text-center'>Crie sua conta</h3>
                    </div>
                </div>

                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-12 col-lg-6 col-xl-6 container'>
                        <span>Usu√°rio:</span>
                        <input autofocus='autofocus' ref={this.username} type='text' className='form-control' maxLength='25' name='username' value={state.username} />
                    </div>
                </div>

                <br />

                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-12 col-lg-6 col-xl-6 container'>
                        <span>Senha:</span>
                        <input type='password' className='form-control' maxLength='16' name='password' value={state.password} />
                    </div>
                </div>

                <br />

                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-12 col-lg-6 col-xl-6 container'>
                        <span>E-mail:</span>
                        <input type='mail' className='form-control' maxLength='100' name='mail' value={state.mail} />
                    </div>
                </div>                

                <br />
    
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-12 col-lg-6 col-xl-6 container'>
                         <button className='btn btn-primary'>Enviar</button>
                         <button className='btn btn-danger'>Limpar</button>
                    </div>
                </div>  
            </div>
        )
    }
}
