import React from 'react';
import context from '../services/context';
import '../assets/css/webchat.scss';
import obj from '../services/loading';
import {SOCKETIP as SOCKETIP} from '../services/server-access-configuration';

import openSocket from 'socket.io-client';
import { get } from 'https';
var $ = require('jquery');

export default class WebChat extends React.Component {

    static contextType = context;

    static defaultProps = { }

    constructor(props) {

        super(props);
    
        this.state = { 
            message:'',
            messages:[],
            to:'todos',
            usersconnected:['todos'],
        };

        this.handleChange = this.handleChange.bind(this);
        this.sendMessage = this.sendMessage.bind(this);
        this.socket = openSocket(SOCKETIP);
        this.message = React.createRef();
    }


    componentDidMount() {

         (function(self,obj) {

            if(!self.context.username) {
                window.location.href = "#/";
                alert('Por favor, logue novamente para continuar!');
                return false;
            }

            self.socket.on('welcome',function(data) {
                obj.closeLoading();

                let username = self.context.username;
                let date = new Date();
                let result_date = date.getDate() + 
                                        "/" +
                                         (date.getMonth() + 1) + 
                                         "/" + 
                                         date.getFullYear() + 
                                         " " + 
                                         date.getHours() + 
                                         ":" + 
                                         date.getMinutes() + 
                                         ":" + 
                                         date.getSeconds();

                
                var send = {
                    from:username,
                    message:data,
                    first:true,
                    date:result_date
                };

                self.socket.emit('register_username',username);
                self.socket.emit('send_message',JSON.stringify(send));
            });

            self.socket.on('receive_message',(data) => {

                var result = JSON.parse(data);
                self.setState({messages:[]});
                self.setState({messages:result});
    
            });

            self.socket.on('get_usersconnected',(data) => {

                var result = JSON.parse(data);
                self.setState({usersconnected:[]});
                self.setState({usersconnected:result});

                console.warn(self.state.usersconnected);
            });

         })(this,obj);   
    }

    handleChange(event) {
        const target = event.target,
              name = target.name,
              value = target.value;

        this.setState({[name]:value});
    }

    sendMessage() {

        if (!this.state.message) {
            return;
        }

        let date = new Date();
        let result_date = date.getDate() + 
                                "/" +
                                 (date.getMonth() + 1) + 
                                 "/" + 
                                 date.getFullYear() + 
                                 " " + 
                                 date.getHours() + 
                                 ":" + 
                                 date.getMinutes() + 
                                 ":" + 
                                 date.getSeconds();

        let send = {
            from:this.context.username,
            message:this.state.message,
            to:this.state.to,
            date:  result_date
        };

        this.socket.emit('send_message',JSON.stringify(send));

        this.setState({message:''});
        this.message.current.focus();
    }

    onKeyDown_Message(event) {

        (function(self,event) {

            if (event.ctrlKey && event.keyCode == 13) {
            
                self.sendMessage();
            }

        })(this,event);
    }

    componentDidCatch() {
        $("footer").removeClass("footer-fixed");
    }

    render() {

        const {state} = this;

        return (
            <div>

                { /* Div com as mensagens */  }
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                        <div className='container-messages--style'>
                       
                                <div>

                                    {state.messages.map((m, key) => {
                                            
                                        {
                                            if(m.first)
                                               return(<div key={key}><p><b style={{color:'reoranged'}}>Servidor</b> disse: <b style={{color:'orange'}}>{m.from}</b> entrou na sala! - <span style={{color:'red'}}>{m.date}</span></p></div>)
                                            else if(m.disconnected) 
                                               return(<div key={key}><p><b style={{color:'reoranged'}}>Servidor</b> disse: <b style={{color:'orange'}}>{m.from}</b> saiu da sala! - <span style={{color:'red'}}>{m.date}</span></p></div>)
                                            else
                                               return(<div key={key}><p><span style={{color:'red'}}>{m.from}</span> disse para <span style={{color:'indigo'}}>{m.to}</span>: <span style={{color:'orange'}}>{m.message} - <span style={{color:'red'}}>{m.date}</span></span></p></div>)
                                        }   

                                      
                                        })}
                                    <br />
                                </div>
                        


                        </div>
                    </div>
                </div>
                <br />
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                      <span>Bem vindo: <b style={{color:'Red'}}>{this.context.username}</b></span>
                    </div>
                </div>
                    <br />
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                      <span>
                            Enviar mensagem para: 
                            <select onChange={this.handleChange} name='to' className='form-control'>

                                    {
                                        state.usersconnected.map((res,key) => {
                                            return (<option value={key}>{res}</option> )
                                        })
                                    }

                            </select> 
                        </span>
                    </div>
                </div>
                <br />
                <div className='row'>
                   <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                       <textarea value={state.message} ref={this.message} onKeyDown={this.onKeyDown_Message.bind(this)} onChange={this.handleChange} name='message' autoFocus='autoFocus' maxLength='1000' cols='10' rows='10' className='form-control' placeHolder='Publique alguma coisa - Ctrl + Enter para enviar'></textarea>
                    </div>
                </div>              
                <br />
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                      <button onClick={this.sendMessage} className='btn btn-primary'>Enviar</button>
                    </div>
                </div>  

            </div>
        )
    }
}
