import React from 'react';
import context from '../services/context';
import '../assets/css/webchat.scss';
import obj from '../services/loading';
import {SOCKETIP as SOCKETIP} from '../services/server-access-configuration';

import openSocket from 'socket.io-client';
import { get } from 'https';
var $ = require('jquery');

export default class WebChat extends React.Component {

    static contextType = context;

    static defaultProps = { }

    constructor(props) {

        super(props);
    
        this.state = { 
            message:'',
            messages:[],
            to:''
        };

        this.handleChange = this.handleChange.bind(this);
        this.sendMessage = this.sendMessage.bind(this);
        this.receiveMessages = this.receiveMessages.bind(this);
        this.socket = openSocket(SOCKETIP);
    }


    componentDidMount() {

         (function(self,obj) {

            self.socket.on('welcome',function(data) {
                obj.closeLoading();

                let username = self.context.username;
                let date = new Date();
                let result_date = date.getDate() + 
                                        "/" +
                                         (date.getMonth() + 1) + 
                                         "/" + 
                                         date.getFullYear() + 
                                         " " + 
                                         date.getHours() + 
                                         ":" + 
                                         date.getMinutes() + 
                                         ":" + 
                                         date.getSeconds();

                
                var send = {
                    from:username,
                    message:data,
                    first:true,
                    date:result_date
                };

                self.socket.emit('send_message',JSON.stringify(send));
            });

            self.socket.on('receive_message',(data) => {

                self.state.messages.length = 0;
                self.setState({messages:new Array()});

                var result = JSON.parse(data);
                self.setState({messages:result});
            });

         })(this,obj);   
    }

    handleChange(event) {
        const target = event.target,
              name = target.name,
              value = target.value;

        this.setState({[name]:value});
    }

    sendMessage() {

        let date = new Date();
        let result_date = date.getDate() + 
                                "/" +
                                 (date.getMonth() + 1) + 
                                 "/" + 
                                 date.getFullYear() + 
                                 " " + 
                                 date.getHours() + 
                                 ":" + 
                                 date.getMinutes() + 
                                 ":" + 
                                 date.getSeconds();

        let send = {
            from:this.context.username,
            message:this.state.message,
            to:this.state.to,
            date:  result_date
        };

        this.socket.emit('send_message',JSON.stringify(send));
    }

    componentDidCatch() {
        $("footer").removeClass("footer-fixed");
    }

    render() {

        const {state} = this;
    

        return (
            <div>

                { /* Div com as mensagens */  }
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                        <div className='container-messages--style'>
                         {
                            state.messages.map((res,index) => {

                                return (<div key={index}>

                                          {state.messages.map((m, key) => {
                                            
                                            return (m.first ? <p><span style={{color:'red'}}>Servidor</span> disse: <span style={{color:'orange'}}>{m.from}</span> entrou na sala! - <span style={{color:'red'}}>{m.date}</span></p>:<p><span style={{color:'red'}}>{m.from}</span> disse: <span style={{color:'orange'}}>{m.message} - <span style={{color:'red'}}>{m.date}</span></span></p>)

                                          })}
                                        <br />
                                    </div>)
                            })
                        }


                        </div>
                    </div>
                </div>
                <br />
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                      <span>Bem vindo: <b style={{color:'Red'}}>{this.context.username}</b></span>
                    </div>
                </div>
                    <br />
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                      <span>
                            Enviar mensagem para: 
                            <select onChange={this.handleChange} name='to' className='form-control'>
                                <option value='0'>Todos</option>                            
                            </select> 
                        </span>
                    </div>
                </div>
                <br />
                <div className='row'>
                   <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                       <textarea onChange={this.handleChange} name='message' autoFocus='autoFocus' maxLength='1000' cols='10' rows='10' className='form-control' placeHolder='Publique alguma coisa - Ctrl + Enter para enviar'></textarea>
                    </div>
                </div>              
                <br />
                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-8 col-lg-8 col-xl-8 container'>
                      <button onClick={this.sendMessage} className='btn btn-primary'>Enviar</button>
                    </div>
                </div>  

            </div>
        )
    }
}
