import React from 'react';
import {Link} from 'react-router-dom';
import logo from '../assets/imgs/logo.png';

class LoginComponent extends React.Component {

    constructor(props) {
        super(props);


        this.state = {
            username:'',
            password:'',
            msg:'',
        };

        this.handleChange = this.handleChange.bind(this);
        this.send = this.send.bind(this);
    }

    handleChange(event) {

        const target = event.target,
             name = target.name,
             value = target.value;

        this.setState({[name]:value});
    }

    send() {

    }

    render() {

        const {state} = this;

        return (
            <div>
                <img src={logo} className='container img-responsive' style={{width:'350px',height:'auto'}}></img>

                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-6 col-xl-6 col-lg-6'>
                      <br />
                      <h3>Painel de Acesso</h3>
                      <br />          
                    </div>
                </div>

                <div className='row'>
                 <div className='col-12 col-sm-12 col-md-6 col-xl-6 col-lg-6'>
                        <p>Preencha corretamente todos os campos abaixo:</p>       
                    </div>
                </div>   
                                      
                <div className='row'>
                     <div className='col-12 col-sm-12 col-md-6 col-xl-6 col-lg-6'>
                        <br/>
                        <span>Usuário:</span>
                        <input name='username' 
                                onChange={this.handleChange}
                                type='text' 
                                className='form-control' 
                                maxLength='25' 
                                placeholder='Informe o nome de usuário' />      
                    </div>
                </div>                 

                <div className='row'>
                    <div className='col-12 col-sm-12 col-md-6 col-xl-6 col-lg-6'>
                        <br />
                        <span>Senha:</span>
                        <input name='password'  
                                onChange={this.handleChange} 
                                type='password' 
                                className='form-control' 
                                maxLength='16' 
                                placeholder='Informe a senha de usuário' />   
                    </div>
                  
                </div>  

                <div className='row'>
                     <div className='col-12 col-sm-12 col-md-2 col-xl-2 col-lg-2'>
                        <br />
                        <button className='btn btn-primary'>Acessar</button> 
                        <br /> 
                        <label>{state.msg}</label>
                    </div>
                    <div className='col-12 col-sm-12 col-md-6 col-xl-6 col-lg-6'>
                   
                        <span  style={{top:'50%'}}>Não tem uma conta? <Link to='/register'>clique aqui</Link></span>  
                    </div>                     
                </div>                  

            </div>
        )
    }

};

export default LoginComponent;
